ValidationRule.errorConditionFormula:
RecordType.DeveloperName == "USPInquiry" &&
ISPICKVAL(Status,"Cerrado") && ISPICKVAL(Type,"Venta") && 
(
( ISPICKVAL(Subtype__c,"Cotización") && (ISPICKVAL(CloseResult__c ,"Declinada - Actividad") || ISPICKVAL(CloseResult__c ,"Declinada - Precio") || ISPICKVAL(CloseResult__c ,"Declinada - Medidas protección") ) 
||  
(ISPICKVAL(Subtype__c,"Gestión Competencias")  &&   
NOT(
(
ISPICKVAL(CloseResult__c ,"Aprobada - Ajuste de precio") || ISPICKVAL(CloseResult__c ,"Aprobada - Modificación Garantías") ||  ISPICKVAL(CloseResult__c ,"Aprobada - Modificación Capitales") || ISPICKVAL(CloseResult__c ,"Aprobada - Modificación Garantías") ||  ISPICKVAL(CloseResult__c ,"Aprobada - Emisión Póliza")   ||  ISPICKVAL(CloseResult__c ,"Aprobada - Modificación Clausulado") || ISPICKVAL(CloseResult__c ,"Aprobada - Suplemento no económico") || ISPICKVAL(CloseResult__c ,"Aprobada - Protecciones del riesgo") || ISPICKVAL(CloseResult__c ,"No Realizado USP") 
)
)
)
)     
)&&
  ISBLANK(CIFNIF__c)   && $Permission.USPTriggerCaseValidationRules
