ValidationRule.errorConditionFormula:
AND(
    RecordType.DeveloperName = "ZRMInquiry",
				IF(ISNEW(),
				    NOT(AND(
           PolicyEndDate__c <= TODAY() + 60, 
           PolicyEndDate__c >= TODAY() - 60
        )),
					   AND(
           PRIORVALUE(PolicyEndDate__c) <= TODAY() + 60, 
           PRIORVALUE(PolicyEndDate__c) >= TODAY() - 60
        )
    )
)
