ValidationRule.errorConditionFormula:
AND(
    RecordType.DeveloperName = "ZRMInquiry",
    NOT(
        OR(
            DATETIMEVALUE(CreatedDate) >= (DATETIMEVALUE(DATE(YEAR(PolicyEndDate__c), MONTH(PolicyEndDate__c), 1)) - 60), /* 2 meses antes */
            DATETIMEVALUE(CreatedDate) <= (DATETIMEVALUE(DATE(YEAR(PolicyEndDate__c), MONTH(PolicyEndDate__c), 1)) + 61) /* 2 meses después */
        )
    )
)
